 ADBK_NM = #{adbkNm}, OTHBC_SCOPE = #{othbcScope}, USE_AT = #{useAt},
 LAST_UPDT_PNTTM = SYSDATE(),  LAST_UPDUSR_ID = #{lastUpdusrId}
