mapper.insert:

    INSERT 
      INTO  COMTNDAMMAPKNO
         (  KNWLDG_TY_CODE
         ,  ORGNZT_ID
         ,	EXPERT_ID
         ,	KNWLDG_TY_NM
         ,  CL_DE
         ,  KNWLDG_URL
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{knoTypeCd}
         ,  #{orgnztId}
         ,  #{speId}
         ,  #{knoTypeNm}		         		         
         ,  #{clYmd}
         ,  #{knoUrl}
         ,  #{frstRegisterId}
         ,  sysdate()		         		         
         ,  #{lastUpdusrId}
         ,  sysdate()
         )

mapper.update:

UPDATE  COMTNDAMMAPKNO 
   SET  ORGNZT_ID         = #{orgnztId}
     ,  EXPERT_ID        	  = #{speId}
     ,  KNWLDG_TY_NM		  = #{knoTypeNm}
     ,  CL_DE  		  = #{clYmd}
     ,  KNWLDG_URL           = #{knoUrl}               
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}                 
     ,  LAST_UPDT_PNTTM = sysdate()
 WHERE  KNWLDG_TY_CODE       = #{knoTypeCd}

mapper.select:
SELECT COUNT(*)
  FROM COMTNDAMMAPKNO
 WHERE KNWLDG_TY_CODE = #{knoTypeCd}
