mapper.select:
SELECT C.ORGNZT_NM ORGNZT_NM , B.KNWLDG_TY_NM KNO_TYPE_NM , A.KNWLDG_ID KNO_ID , A.KNWLDG_NM KNO_NM , D.USER_NM USER_NM , TO_CHAR(TO_DATE(A.COLCT_DE,'YYYYmmdd'),'YYYY-mm-dd') COL_YMD , A.OTHBC_AT OTHBC_AT , A.FRST_REGISTER_ID FRST_REGISTER_ID , A.FRST_REGIST_PNTTM FRST_REGIST_PNTTM , A.LAST_UPDUSR_ID LAST_UPDUSR_ID , A.LAST_UPDT_PNTTM LAST_UPDUSR_PNTTM FROM COMTNDAMKNOIFM A, COMTNDAMMAPKNO B, COMTNDAMMAPTEAM C, COMVNUSERMASTER D WHERE C.ORGNZT_ID = B.ORGNZT_ID AND A.KNWLDG_TY_CODE = B.KNWLDG_TY_CODE AND A.FRST_REGISTER_ID = D.ESNTL_ID AND A.FRST_REGISTER_ID = #{frstRegisterId}
AND B.KNWLDG_TY_NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.KNWLDG_NM LIKE CONCAT('%', #{searchKeyword}, '%')
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNDAMKNOIFM A, COMTNDAMMAPKNO B, COMTNDAMMAPTEAM C, COMVNUSERMASTER D WHERE C.ORGNZT_ID = B.ORGNZT_ID AND A.KNWLDG_TY_CODE = B.KNWLDG_TY_CODE AND A.FRST_REGISTER_ID = D.ESNTL_ID AND A.FRST_REGISTER_ID = #{frstRegisterId}
AND B.KNWLDG_TY_NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.KNWLDG_NM LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.insert:

    INSERT 
      INTO  COMTNDAMKNOIFM
         (  KNWLDG_ID
         ,  KNWLDG_NM
         ,	ORGNZT_ID
         ,  KNWLDG_TY_CODE
         ,  KNWLDG_CN
         ,  COLCT_DE
         ,  OTHBC_AT
         ,  ATCH_FILE_ID
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{knoId}
         ,  #{knoNm}
         ,  #{orgnztId}		         		         
         ,  #{knoTypeCd}
         ,  #{knoCn}
         ,  #{colYmd}
         ,  #{othbcAt}
         ,  #{atchFileId}		         		         
         ,  #{frstRegisterId}
         ,  NOW()		         		         
         ,  #{lastUpdusrId}
         ,  NOW()
         )

mapper.update:

            UPDATE  COMTNDAMKNOIFM 
               SET  ORGNZT_ID         = #{orgnztId}      
                 ,  KNWLDG_TY_CODE		  = #{knoTypeCd}
                 ,  KNWLDG_NM        	  = #{knoNm}
                 ,  KNWLDG_CN			  = #{knoCn}
		         ,  COLCT_DE			  = REPLACE(#{colYmd},'-','')
		         ,  OTHBC_AT          = #{othbcAt}               
                 ,  ATCH_FILE_ID      = #{atchFileId}               
                 ,  LAST_UPDUSR_ID    = #{lastUpdusrId}               
                 ,  LAST_UPDT_PNTTM = NOW()
             WHERE  KNWLDG_ID       	  = #{knoId}
