mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT C.ORGNZT_NM , B.KNWLDG_TY_CODE , B.KNWLDG_TY_NM , D.USER_NM , A.EXPERT_GRAD , TO_CHAR(TO_DATE(A.EXPERT_CONFM_DE, 'YYYYmmdd'), 'YYYY-mm-dd') AS EXPERT_CONFM_DE , A.EXPERT_ID , A.FRST_REGISTER_ID , A.FRST_REGIST_PNTTM , A.LAST_UPDUSR_ID , A.LAST_UPDT_PNTTM FROM COMTNDAMPRO A, COMTNDAMMAPKNO B, COMTNDAMMAPTEAM C, COMVNUSERMASTER D WHERE C.ORGNZT_ID = B.ORGNZT_ID AND A.KNWLDG_TY_CODE = B.KNWLDG_TY_CODE AND A.EXPERT_ID = D.ESNTL_ID
AND D.USER_NM LIKE '%'||#{searchKeyword}||'%'
AND B.KNWLDG_TY_NM LIKE '%'||#{searchKeyword}||'%'
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.insert:


    INSERT 
      INTO  COMTNDAMPRO
         (  EXPERT_ID
         ,  KNWLDG_TY_CODE
         ,  EXPERT_GRAD
         ,  EXPERT_DC
         ,  EXPERT_CONFM_DE
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{speId}         		         
         ,  #{knoTypeCd}
         ,  #{appTypeCd}
         ,  #{speExpCn}
         ,  #{speConfmDe}        		         
         ,  #{frstRegisterId}
         ,  SYSDATETIME		         		         
         ,  #{lastUpdusrId}
         ,  SYSDATETIME
         )

mapper.update:

UPDATE  COMTNDAMPRO 
   SET  EXPERT_GRAD       = #{appTypeNm}
     ,  EXPERT_DC	      = #{speExpCn} 
     ,  EXPERT_CONFM_DE	  = #{speConfmDe}                            
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}               
     ,  LAST_UPDT_PNTTM = SYSDATETIME
WHERE  EXPERT_ID       	  = #{speId}
AND    KNWLDG_TY_CODE 		  = #{knoTypeCd}
AND    EXPERT_GRAD		  = #{appTypeCd}

mapper.delete:

DELETE  FROM COMTNDAMPRO 
WHERE   EXPERT_ID 		= #{speId}
AND     KNWLDG_TY_CODE = #{knoTypeCd}
AND     EXPERT_GRAD = #{appTypeCd}
