mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT A.DEPT_CODE, A.DEPT_NM, A.USER_ID, A.USER_NM, B.AUTHOR_CODE, A.ESNTL_ID, (CASE WHEN B.SCRTY_DTRMN_TRGET_ID IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN FROM (SELECT A.ORGNZT_ID DEPT_CODE, A.ORGNZT_NM DEPT_NM, B.EMPLYR_ID USER_ID, B.USER_NM USER_NM, B.ESNTL_ID FROM COMTNORGNZTINFO A, COMTNEMPLYRINFO B WHERE A.ORGNZT_ID = B.ORGNZT_ID AND A.ORGNZT_ID = #{deptCode}) A LEFT OUTER JOIN COMTNEMPLYRSCRTYESTBS B ON A.ESNTL_ID = B.SCRTY_DTRMN_TRGET_ID WHERE 1 = 1
) ALL_LIST )
WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.update:

UPDATE COMTNEMPLYRSCRTYESTBS 
   SET AUTHOR_CODE=#{authorCode}
 WHERE SCRTY_DTRMN_TRGET_ID=#{uniqId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT ORGNZT_ID AS DEPT_CODE, ORGNZT_NM AS DEPT_NM FROM COMTNORGNZTINFO WHERE 1 = 1
AND ORGNZT_NM LIKE '%'||#{searchKeyword}||'%'
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}
