mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT A.ROLE_CODE, A.ROLE_NM, A.ROLE_PTTRN, A.ROLE_DC, A.ROLE_TY, A.ROLE_SORT, B.AUTHOR_CODE, B.CREAT_DT, (CASE WHEN B.ROLE_CODE IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN FROM COMTNROLEINFO A LEFT OUTER JOIN (SELECT AUTHOR_CODE , ROLE_CODE, CREAT_DT FROM COMTNAUTHORROLERELATE WHERE AUTHOR_CODE = #{searchKeyword}) B ON A.ROLE_CODE = B.ROLE_CODE
) ALL_LIST )
WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.delete:

DELETE FROM COMTNAUTHORROLERELATE 
 WHERE AUTHOR_CODE=#{authorCode}
   AND ROLE_CODE=#{roleCode}
