mapper.insert:

			INSERT INTO COMTNROLEINFO 
				  ( ROLE_CODE
				  , ROLE_NM
                  , ROLE_PTTRN
                  , ROLE_DC
                  , ROLE_TY
				  , ROLE_SORT
				  , ROLE_CREAT_DE )
		   VALUES ( #{roleCode}
			      , #{roleNm}
				  , #{rolePtn}
                  , #{roleDc}
                  , #{roleTyp}
                  , #{roleSort}
				  , TO_CHAR(NOW(), 'YYYY-mm-dd HH24:MI:SS'))

mapper.update:

			UPDATE COMTNROLEINFO 
			   SET ROLE_NM=#{roleNm}
                 , ROLE_PTTRN=#{rolePtn}
                 , ROLE_DC=#{roleDc}
                 , ROLE_TY=#{roleTyp}
                 , ROLE_SORT=#{roleSort}
				 , ROLE_CREAT_DE=TO_CHAR(NOW(), 'YYYY-mm-dd HH24:MI:SS')
		  	 WHERE ROLE_CODE=#{roleCode}
