mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT ROLE_CODE, ROLE_NM, ROLE_PTTRN, ROLE_DC, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM029' AND CODE = ROLE_TY) AS ROLE_TY, ROLE_SORT, ROLE_CREAT_DE FROM COMTNROLEINFO WHERE 1=1
AND ROLE_NM LIKE '%'||#{searchKeyword}||'%'
ORDER BY ROLE_CREAT_DE DESC
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

 INSERT INTO COMTNROLEINFO 
       ( ROLE_CODE
       , ROLE_NM
       , ROLE_PTTRN
       , ROLE_DC
       , ROLE_TY
       , ROLE_SORT
       , ROLE_CREAT_DE )
VALUES ( #{roleCode}
       , #{roleNm}
       , #{rolePtn}
       , #{roleDc}
       , #{roleTyp}
       , #{roleSort}
       , SYSDATE)

mapper.update:

UPDATE COMTNROLEINFO 
   SET ROLE_NM=#{roleNm}
     , ROLE_PTTRN=#{rolePtn}
     , ROLE_DC=#{roleDc}
     , ROLE_TY=#{roleTyp}
     , ROLE_SORT=#{roleSort}
     , ROLE_CREAT_DE=SYSDATE
 WHERE ROLE_CODE=#{roleCode}
