mapper.select:

    SELECT  *
      FROM  (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM  (

/* 구현 Sql */

    SELECT  INSTT_ID
         ,  INSTT_NM
      FROM  COMTNCNTCINSTT
     WHERE  USE_AT = 'Y'

AND
    INSTT_NM LIKE '%' || #{searchKeyword} || '%'

AND
    1 = 1

/* 구현 Sql */

            ) ALL_LIST
            )
     WHERE  RNUM  > #{firstIndex}
       AND  RNUM <= #{firstIndex} + #{recordCountPerPage}


    SELECT  *
      FROM  (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM  (

/* 구현 Sql */

    SELECT  INSTT_ID
         ,  SYS_ID
         ,  SYS_NM
         ,  SYS_IP
      FROM  COMTNCNTCSYSTEM
     WHERE  USE_AT = 'Y'

AND
    INSTT_ID = #{searchKeyword}

AND
    INSTT_ID = #{insttId}

/* 구현 Sql */

            ) ALL_LIST
            )
     WHERE  RNUM  > #{firstIndex}
       AND  RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNCNTCSYSTEM WHERE USE_AT = 'Y'
AND INSTT_ID = #{searchKeyword}
AND INSTT_ID = #{insttId}


    SELECT  *
      FROM  (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM  (

/* 구현 Sql */

    SELECT  INSTT_ID
         ,  SYS_ID
         ,  SVC_ID
         ,  SVC_NM
         ,  REQUST_MSSAGE_ID	REQUEST_MESSAGE_ID
         ,  RSPNS_MSSAGE_ID		RSPNS_MESSAGE_ID
      FROM  COMTNCNTCSERVICE
     WHERE  USE_AT = 'Y'

AND
    SVC_ID = #{searchKeyword}

AND
    INSTT_ID = #{insttId}

AND
    SYS_ID = #{sysId}

AND
    INSTT_ID = #{insttId}

/* 구현 Sql */

            ) ALL_LIST
            )
     WHERE  RNUM  > #{firstIndex}
       AND  RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNCNTCSYSTEM WHERE USE_AT = 'Y'
AND SYS_NM LIKE '%' || #{searchKeyword} || '%'
AND INSTT_ID = #{insttId}

mapper.insert:

INSERT
  INTO  COMTNCNTCINSTT
     (  INSTT_ID
     ,  INSTT_NM
     ,  USE_AT
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{insttId}
     ,  #{insttNm}
     ,  'Y'
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )


INSERT
  INTO  COMTNCNTCSYSTEM
     (  INSTT_ID
     ,  SYS_ID
     ,  SYS_NM
     ,  SYS_IP
     ,  USE_AT
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{insttId}
     ,  #{sysId}
     ,  #{sysNm}
     ,  #{sysIp}
     ,  'Y'
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )


INSERT
  INTO  COMTNCNTCSERVICE
     (  INSTT_ID
     ,  SYS_ID
     ,  SVC_ID
     ,  SVC_NM
     ,  REQUST_MSSAGE_ID
     ,  RSPNS_MSSAGE_ID
     ,  USE_AT
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{insttId}
     ,  #{sysId}
     ,  #{svcId}
     ,  #{svcNm}
     ,  #{requestMessageId}
     ,  #{rspnsMessageId}
     ,  'Y'
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )

mapper.update:

UPDATE  COMTNCNTCINSTT
   SET  INSTT_NM           = #{insttNm}
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}


UPDATE  COMTNCNTCSYSTEM
   SET  SYS_NM             = #{sysNm}
     ,  SYS_IP             = #{sysIp}
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}
   AND  SYS_ID             = #{sysId}


UPDATE  COMTNCNTCSERVICE
   SET  SVC_NM             = #{svcNm}
     ,  REQUST_MSSAGE_ID = #{requestMessageId}
     ,  RSPNS_MSSAGE_ID   = #{rspnsMessageId}
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}
   AND  SYS_ID             = #{sysId}
   AND  SVC_ID             = #{svcId}

mapper.delete:

UPDATE  COMTNCNTCINSTT
   SET  USE_AT             = 'N'
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}


UPDATE  COMTNCNTCSYSTEM
   SET  USE_AT             = 'N'
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}
   AND  SYS_ID             = #{sysId}


UPDATE  COMTNCNTCSERVICE
   SET  USE_AT             = 'N'
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  INSTT_ID           = #{insttId}
   AND  SYS_ID             = #{sysId}
   AND  SVC_ID             = #{svcId}
