mapper.select:

    SELECT  *
      FROM  (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM  (

/* 구현 Sql */

    SELECT  CNTC_MSSAGE_ID			CNTC_MESSAGE_ID
         ,  CNTC_MSSAGE_NM			CNTC_MESSAGE_NM
         ,  UPPER_CNTC_MSSAGE_ID	UPPER_CNTC_MESSAGE_ID
      FROM  COMTNCNTCMESSAGE
     WHERE  USE_AT = 'Y'

AND

    	CNTC_MSSAGE_NM LIKE '%' || #{searchKeyword} || '%'


AND
    1 = 1

/* 구현 Sql */

            ) ALL_LIST
            )
     WHERE  RNUM  > #{firstIndex}
       AND  RNUM <= #{firstIndex} + #{recordCountPerPage}


    SELECT  *
      FROM  (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM  (

/* 구현 Sql */

    SELECT  CNTC_MSSAGE_ID			CNTC_MESSAGE_ID
         ,  IEM_ID					ITEM_ID
         ,  IEM_NM					ITEM_NM
         ,  IEM_TY					ITEM_TYPE
         ,  IEM_LT					ITEM_LT
      FROM  COMTNCNTCMESSAGEITEM
     WHERE  USE_AT = 'Y'

AND

    	IEM_NM LIKE '%' || #{searchKeyword} || '%'


AND

    	CNTC_MSSAGE_ID = #{cntcMessageId}


/* 구현 Sql */

            ) ALL_LIST
            )
     WHERE  RNUM  > #{firstIndex}
       AND  RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT
  INTO  COMTNCNTCMESSAGE
     (  CNTC_MSSAGE_ID
     ,  CNTC_MSSAGE_NM
     ,  UPPER_CNTC_MSSAGE_ID
     ,  USE_AT
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{cntcMessageId}
     ,  #{cntcMessageNm}
     ,  #{upperCntcMessageId}
     ,  'Y'
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )


INSERT
  INTO  COMTNCNTCMESSAGEITEM
     (  CNTC_MSSAGE_ID
     ,  IEM_ID
     ,  IEM_NM
     ,  IEM_TY
     ,  IEM_LT
     ,  USE_AT
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{cntcMessageId}
     ,  #{itemId}
     ,  #{itemNm}
     ,  #{itemType}
     ,  #{itemLt}
     ,  'Y'
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )

mapper.update:

UPDATE  COMTNCNTCMESSAGE
   SET  CNTC_MSSAGE_NM       = #{cntcMessageNm}
     ,  UPPER_CNTC_MSSAGE_ID = #{upperCntcMessageId}
     ,  LAST_UPDUSR_ID        = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM     = sysdate
 WHERE  CNTC_MSSAGE_ID       = #{cntcMessageId}


UPDATE  COMTNCNTCMESSAGEITEM
   SET  IEM_NM           = #{itemNm}
     ,  IEM_TY         = #{itemType}
     ,  IEM_LT           = #{itemLt}
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM = sysdate
 WHERE  CNTC_MSSAGE_ID   = #{cntcMessageId}
   AND  IEM_ID           = #{itemId}

mapper.delete:

UPDATE  COMTNCNTCMESSAGE
   SET  USE_AT            = 'N'
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM = sysdate
 WHERE  CNTC_MSSAGE_ID   = #{cntcMessageId}


UPDATE  COMTNCNTCMESSAGEITEM
   SET  USE_AT            = 'N'
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM = sysdate
 WHERE  CNTC_MSSAGE_ID   = #{cntcMessageId}
   AND  IEM_ID           = #{itemId}
