mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT CNTC_ID , CNTC_NM , CNTC_TY , PROVD_INSTT_ID , PROVD_SYS_ID , PROVD_SVC_ID , REQUST_INSTT_ID , REQUST_SYS_ID , CONFM_AT , USE_AT , VALID_BGNDE , VALID_ENDDE FROM COMTNSYSTEMCNTC WHERE 1 = 1
AND CNTC_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */
) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT
  INTO  COMTNSYSTEMCNTC
     (  CNTC_ID
     ,  CNTC_NM
     ,  CNTC_TY
     ,  PROVD_INSTT_ID
     ,  PROVD_SYS_ID
     ,  PROVD_SVC_ID
     ,  REQUST_INSTT_ID
     ,  REQUST_SYS_ID
     ,  CONFM_AT
     ,  USE_AT
     ,  VALID_BGNDE
     ,  VALID_ENDDE
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{cntcId}
     ,  #{cntcNm}
     ,  #{cntcType}
     ,  #{provdInsttId}
     ,  #{provdSysId}
     ,  #{provdSvcId}
     ,  #{requstInsttId}
     ,  #{requstSysId}
     ,  #{confmAt}
     ,  #{useAt}
     ,  #{validBeginDe}
     ,  #{validEndDe}
     ,  #{frstRegisterId}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     ,  SYSDATETIME
     )

mapper.update:

UPDATE  COMTNSYSTEMCNTC
   SET  CNTC_NM           = #{cntcNm}
     ,  CNTC_TY           = #{cntcType}
     ,  PROVD_INSTT_ID    = #{provdInsttId}
     ,  PROVD_SYS_ID      = #{provdSysId}
     ,  PROVD_SVC_ID      = #{provdSvcId}
     ,  REQUST_INSTT_ID   = #{requstInsttId}
     ,  REQUST_SYS_ID     = #{requstSysId}
     ,  CONFM_AT          = #{confmAt}
     ,  USE_AT            = #{useAt}
     ,  VALID_BGNDE    = #{validBeginDe}
     ,  VALID_ENDDE      = #{validEndDe}
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM = SYSDATETIME
 WHERE  CNTC_ID = #{cntcId}


UPDATE  COMTNSYSTEMCNTC
   SET  CONFM_AT          = #{confmAt}
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM = SYSDATETIME
 WHERE  CNTC_ID = #{cntcId}
