mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( SELECT A.BBS_ID, B.BBS_NM, A.NTT_ID, C.NTT_SJ, A.ATCH_FILE_ID, A.FILE_SN, D.ORIGNL_FILE_NM, COUNT(*) AS COUNT FROM COMTNDTAUSESTATS A, COMTNBBSMASTER B, COMTNBBS C, COMTNFILEDETAIL D WHERE 1 = 1 AND A.BBS_ID = B.BBS_ID AND B.BBS_ID = C.BBS_ID AND A.NTT_ID = C.NTT_ID AND A.ATCH_FILE_ID = D.ATCH_FILE_ID AND A.FILE_SN = D.FILE_SN
AND B.BBS_NM LIKE '%'||#{searchKeyword}||'%'
AND TO_CHAR(A.FRST_REGIST_PNTTM,'YYYYMMDD') BETWEEN #{pmFromDate} AND #{pmToDate}
GROUP BY A.BBS_ID, B.BBS_NM, A.NTT_ID, C.NTT_SJ, A.ATCH_FILE_ID, A.FILE_SN, D.ORIGNL_FILE_NM ) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) AS totcnt FROM COMTNDTAUSESTATS A, COMTNBBSMASTER B, COMTNBBS C, COMTNFILEDETAIL D WHERE 1 = 1 AND A.BBS_ID = B.BBS_ID AND B.BBS_ID = C.BBS_ID AND A.NTT_ID = C.NTT_ID AND A.ATCH_FILE_ID = D.ATCH_FILE_ID AND A.FILE_SN = D.FILE_SN
AND B.BBS_NM LIKE '%'||#{searchKeyword}||'%'
AND TO_CHAR(A.FRST_REGIST_PNTTM,'YYYYMMDD') BETWEEN #{pmFromDate} AND #{pmToDate}

SELECT A.DTA_USE_STATS_ID, A.BBS_ID, B.BBS_NM, A.NTT_ID, C.NTT_SJ, A.ATCH_FILE_ID, A.FILE_SN, D.ORIGNL_FILE_NM, A.FRST_REGISTER_ID, E.USER_NM, A.FRST_REGIST_PNTTM FROM COMTNDTAUSESTATS A, COMTNBBSMASTER B, COMTNBBS C, COMTNFILEDETAIL D, COMVNUSERMASTER E WHERE 1 = 1 AND A.BBS_ID = B.BBS_ID AND A.BBS_ID = C.BBS_ID AND A.NTT_ID = C.NTT_ID AND A.ATCH_FILE_ID = D.ATCH_FILE_ID AND A.FILE_SN = D.FILE_SN AND A.FRST_REGISTER_ID = E.USER_ID AND A.BBS_ID = #{bbsId} AND A.NTT_ID = #{nttId} AND A.ATCH_FILE_ID = #{atchFileId} AND A.FILE_SN = #{fileSn}
AND TO_CHAR(A.FRST_REGIST_PNTTM,'YYYYMMDD') BETWEEN #{pmFromDate} AND #{pmToDate}
ORDER BY A.DTA_USE_STATS_ID DESC

SELECT COUNT(*) AS totcnt FROM COMTNDTAUSESTATS A, COMTNBBSMASTER B, COMTNBBS C, COMTNFILEDETAIL D, COMVNUSERMASTER E WHERE 1 = 1 AND A.BBS_ID = B.BBS_ID AND A.BBS_ID = C.BBS_ID AND A.NTT_ID = C.NTT_ID AND A.ATCH_FILE_ID = D.ATCH_FILE_ID AND A.FILE_SN = D.FILE_SN AND A.FRST_REGISTER_ID = E.USER_ID AND A.BBS_ID = #{bbsId} AND A.NTT_ID = #{nttId} AND A.ATCH_FILE_ID = #{atchFileId} AND A.FILE_SN = #{fileSn}
AND TO_CHAR(A.FRST_REGIST_PNTTM,'YYYYMMDD') BETWEEN #{pmFromDate} AND #{pmToDate}
ORDER BY A.DTA_USE_STATS_ID DESC
