mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT ADMINIST_ZONE_SE , ADMINIST_ZONE_CODE , ADMINIST_ZONE_NM , UPPER_ADMINIST_ZONE_CODE , CREAT_DE , ABL_DE , USE_AT FROM COMTCADMINISTCODE WHERE 1 = 1
AND ADMINIST_ZONE_SE = '1' AND ADMINIST_ZONE_NM LIKE '%' || #{searchKeyword} || '%'
AND ADMINIST_ZONE_SE = '2' AND ADMINIST_ZONE_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */ ) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT
  INTO  COMTCADMINISTCODE
     (  ADMINIST_ZONE_SE
     ,  ADMINIST_ZONE_CODE
     ,  ADMINIST_ZONE_NM
     ,  UPPER_ADMINIST_ZONE_CODE
     ,  CREAT_DE
     ,  ABL_DE
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{administZoneSe}
     ,  #{administZoneCode}
     ,  #{administZoneNm}
     ,  #{upperAdministZoneCode}
     ,  #{creatDe}
     ,  #{ablDe}
     ,  #{useAt}
     ,  SYSDATE
     ,  #{frstRegisterId}
     ,  SYSDATE
     ,  #{frstRegisterId}
     )

mapper.update:

            UPDATE  COMTCADMINISTCODE
               SET  ADMINIST_ZONE_NM         = #{administZoneNm}
			     ,  UPPER_ADMINIST_ZONE_CODE = #{upperAdministZoneCode}
			     ,  CREAT_DE                 = #{creatDe}
			     ,  ABL_DE                   = #{ablDe}
			     ,  USE_AT                   = #{useAt}
                 ,  LAST_UPDT_PNTTM        = sysdate
                 ,  LAST_UPDUSR_ID           = #{lastUpdusrId}
             WHERE  ADMINIST_ZONE_SE   = #{administZoneSe}
               AND  ADMINIST_ZONE_CODE = #{administZoneCode}

mapper.delete:

            UPDATE  COMTCADMINISTCODE
               SET  ABL_DE             = TO_CHAR(SYSDATE,'YYYYMMDD')
			     ,  USE_AT             = 'N'
             WHERE  ADMINIST_ZONE_SE   = #{administZoneSe}
               AND  ADMINIST_ZONE_CODE = #{administZoneCode}
