mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT B.CL_CODE_NM , A.CODE_ID , A.CODE_ID_NM , A.USE_AT FROM COMTCCMMNCODE A , COMTCCMMNCLCODE B WHERE A.CL_CODE = B.CL_CODE
AND A.CODE_ID LIKE '%' || #{searchKeyword} || '%'
AND A.CODE_ID_NM LIKE '%' || #{searchKeyword} || '%'
AND A.CL_CODE LIKE '%' || #{searchKeyword} || '%'
AND A.USE_AT = 'Y'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:


INSERT
  INTO  COMTCCMMNCODE
     (  CODE_ID
     ,  CODE_ID_NM
     ,  CODE_ID_DC
     ,  CL_CODE
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{codeId}
     ,  #{codeIdNm}
     ,  #{codeIdDc}
     ,  #{clCode}
     ,  #{useAt}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     )

mapper.update:


UPDATE  COMTCCMMNCODE
   SET  CODE_ID_NM        = #{codeIdNm}
     ,  CODE_ID_DC        = #{codeIdDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = sysdate
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CODE_ID           = #{codeId}

mapper.delete:


UPDATE  COMTCCMMNCODE
   SET  USE_AT  = 'N'
 WHERE  CODE_ID = #{codeId}
