mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT A.CODE_ID , A.CODE , A.CODE_NM , A.USE_AT FROM COMTCCMMNDETAILCODE A , COMTCCMMNCODE B WHERE B.USE_AT = 'Y' AND A.CODE_ID = B.CODE_ID
AND A.CODE_ID LIKE '%' || #{searchKeyword} || '%'
AND A.CODE LIKE '%' || #{searchKeyword} || '%'
AND A.CODE_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */ ) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:


INSERT
  INTO  COMTCCMMNDETAILCODE
     (  CODE_ID
     ,  CODE
     ,  CODE_NM
     ,  CODE_DC
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{codeId}
     ,  #{code}
     ,  #{codeNm}
     ,  #{codeDc}
     ,  #{useAt}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     )

mapper.update:


UPDATE  COMTCCMMNDETAILCODE
   SET  CODE_NM           = #{codeNm}
     ,  CODE_DC           = #{codeDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = sysdate
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CODE_ID   = #{codeId}
   AND  CODE      = #{code}

mapper.delete:


UPDATE  COMTCCMMNDETAILCODE
   SET  USE_AT    = 'N'
 WHERE  CODE_ID   = #{codeId}
   AND  CODE      = #{code}
