mapper.insert:

SELECT CASE WHEN MAX(SN)+1 IS NULL THEN 1
       ELSE MAX(SN)+1 END AS sn 
FROM  COMTRDNMADRZIP


INSERT
  INTO  COMTRDNMADRZIP
     (  RDMN_CODE
     ,  SN
     ,  CTPRVN_NM
     ,  SIGNGU_NM
     ,  RDMN
     ,  BDNBR_MNNM
     ,  BDNBR_SLNO
     ,  BULD_NM
     ,  DETAIL_BULD_NM
     ,  ZIP
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{rdmnCode}
     ,  #{sn}
     ,  #{ctprvnNm}
     ,  #{signguNm}
     ,  #{rdmn}
     ,  #{bdnbrMnnm}
     ,  #{bdnbrSlno}
     ,  #{buldNm}
     ,  #{detailBuldNm}
     ,  #{zip}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     )


INSERT
  INTO  COMTRDNMADRZIP
     (  RDMN_CODE
     ,  SN
     ,  CTPRVN_NM
     ,  SIGNGU_NM
     ,  RDMN
     ,  BDNBR_MNNM
     ,  BDNBR_SLNO
     ,  BULD_NM
     ,  DETAIL_BULD_NM
     ,  ZIP
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{rdmnCode}
     ,  #{sn}
     ,  #{ctprvnNm}
     ,  #{signguNm}
     ,  #{rdmn}
     ,  #{bdnbrMnnm}
     ,  #{bdnbrSlno}
     ,  #{buldNm}
     ,  #{detailBuldNm}
     ,  #{zip}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  COMTRDNMADRZIP
   SET  CTPRVN_NM       = #{ctprvnNm}
     ,  SIGNGU_NM       = #{signguNm}
     ,  RDMN          	= #{rdmn}
     ,  BDNBR_MNNM      = #{bdnbrMnnm}
     ,  BDNBR_SLNO      = #{bdnbrSlno}
     ,  BULD_NM      	= #{buldNm}
     ,  DETAIL_BULD_NM	= #{detailBuldNm}
     ,  ZIP      		= #{zip}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID  = #{lastUpdusrId}
 WHERE  RDMN_CODE       = #{rdmnCode}
   AND  SN              = #{sn}

mapper.delete:

DELETE
  FROM  COMTRDNMADRZIP
 WHERE  RDMN_CODE = #{rdmnCode}
   AND  SN = #{sn}
