	  , HIST_CN
	  , HIST_ID
	  , FRST_REGISTER_ID
	  , ATCH_FILE_ID
	  , FRST_REGIST_PNTTM )
VALUES ( #{sysNm}
	  , #{histSeCode}
	  , #{histCn}
	  , #{histId}
	  , #{frstRegisterId}
	  , #{atchFileId}
	  , sysdate)

mapper.update:

UPDATE COMTHSYSHIST
   SET SYS_NM = #{sysNm}
	 , HIST_SE_CODE = #{histSeCode}
	 , HIST_CN = #{histCn}
	 , ATCH_FILE_ID = #{atchFileId}
 WHERE HIST_ID = #{histId}
