mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT MENU_ID , EMPLYR_ID, MENU_NM, PROGRM_STRE_PATH FROM COMTNBKMKMENUMANAGERESULT WHERE EMPLYR_ID = #{userId}
AND MENU_NM LIKE '%'|| #{searchWrd}||'%'
ORDER BY MENU_NM
) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT d.MENU_NO, d.MENU_NM, d.MENU_DC FROM COMTNMENUINFO d, (SELECT a.MENU_NO FROM COMTNMENUCREATDTLS a, COMTNEMPLYRSCRTYESTBS b , COMVNUSERMASTER c WHERE a.AUTHOR_CODE = b.AUTHOR_CODE AND b.SCRTY_DTRMN_TRGET_ID = c.ESNTL_ID AND c.USER_ID = #{userId})temp WHERE d.PROGRM_FILE_NM != 'dir' AND d.MENU_NO = temp.MENU_NO AND d.menu_no not in (SELECT MENU_ID FROM COMTNBKMKMENUMANAGERESULT WHERE EMPLYR_ID = #{userId})
AND d.MENU_NM LIKE '%'|| #{searchWrd}||'%'
ORDER BY MENU_NM
) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}
