mapper.insert:

			INSERT INTO COMTNMENUINFO 
				( MENU_NO
            , MENU_ORDR
            , MENU_NM
            , UPPER_MENU_NO
            , MENU_DC
            , RELATE_IMAGE_PATH
            , RELATE_IMAGE_NM
            , PROGRM_FILE_NM )
			VALUES (   #{menuNo}
                  , #{menuOrdr}
                  , #{menuNm}  
                  , #{upperMenuId}
                  , #{menuDc}
                  , #{relateImagePath}
                  , #{relateImageNm}
                  , #{progrmFileNm} )

mapper.update:

			UPDATE COMTNMENUINFO 
			SET  MENU_ORDR         = #{menuOrdr}       
            , MENU_NM           = #{menuNm}         
            , UPPER_MENU_NO     = #{upperMenuId}    
            , MENU_DC           = #{menuDc}         
            , RELATE_IMAGE_PATH = #{relateImagePath}
            , RELATE_IMAGE_NM   = #{relateImageNm}  
            , PROGRM_FILE_NM    = #{progrmFileNm}   
			WHERE MENU_NO=#{menuNo}

mapper.select:

SELECT COUNT(MENU_NO) AS "totcnt"
  FROM COMTNMENUINFO
 WHERE MENU_NO = #{menuNo}


SELECT COUNT(MENU_NO) AS totcnt
  FROM COMTNMENUINFO
 WHERE UPPER_MENU_NO = #{menuNo}
