mapper.insert:

			INSERT INTO COMTHPROGRMCHANGEDTLS
				(  PROGRM_FILE_NM
              , REQUST_NO
              , RQESTER_ID
              , CHANGE_REQUST_CN
              , RQESTDE
              , REQUST_SJ 
              ,PROCESS_STTUS_CODE)
			VALUES (  #{progrmFileNm}
                 , #{rqesterNo}
                 , #{rqesterPersonId}
                 , #{changerqesterCn}
                 , #{rqesterDe}
                 , #{rqesterSj} 
                 , 'A')

mapper.update:

			UPDATE COMTHPROGRMCHANGEDTLS
			SET    RQESTER_ID         = #{rqesterPersonId} 
			     , CHANGE_REQUST_CN   = #{changerqesterCn}
			     , REQUST_PROCESS_CN = #{rqesterProcessCn}
                 , RQESTDE         = #{rqesterDe}       
                 , REQUST_SJ         = #{rqesterSj} 
			WHERE PROGRM_FILE_NM=#{progrmFileNm}
			AND   REQUST_NO    =#{rqesterNo}

mapper.delete:

DELETE FROM COMTHPROGRMCHANGEDTLS
WHERE PROGRM_FILE_NM=#{progrmFileNm}
AND   REQUST_NO    =#{rqesterNo}

mapper.update:

			UPDATE COMTHPROGRMCHANGEDTLS
			SET    CHANGE_REQUST_CN   = #{changerqesterCn}  
			     , REQUST_PROCESS_CN = #{rqesterProcessCn} 
                 , OPETR_ID           = #{opetrId}         
                 , PROCESS_STTUS_CODE = #{processSttus}    
                 , PROCESS_DE         = #{processDe}       
			WHERE  PROGRM_FILE_NM=#{progrmFileNm}
			AND    REQUST_NO    =#{rqesterNo}

mapper.select:
SELECT PROGRM_FILE_NM AS "PROGRM_FILE_NM" , REQUST_NO AS "REQUST_NO" , RQESTER_ID AS "RQESTER_ID" , CHANGE_REQUST_CN AS "CHANGE_REQUST_CN" , REQUST_PROCESS_CN AS "REQUST_PROCESS_CN" , OPETR_ID AS "OPETR_ID" , trim(PROCESS_STTUS_CODE) AS "PROCESS_STTUS_CODE" , PROCESS_DE AS "PROCESS_DE" , RQESTDE AS "RQESTDE" , REQUST_SJ AS "REQUST_SJ" FROM ( SELECT rownum rn , TB.PROGRM_FILE_NM , TB.REQUST_NO , TB.RQESTER_ID , TB.CHANGE_REQUST_CN , TB.REQUST_PROCESS_CN , TB.OPETR_ID , TB.PROCESS_STTUS_CODE , TB.PROCESS_DE , TB.RQESTDE , TB.REQUST_SJ FROM ( SELECT PROGRM_FILE_NM , REQUST_NO , RQESTER_ID , CHANGE_REQUST_CN , REQUST_PROCESS_CN , OPETR_ID , PROCESS_STTUS_CODE , PROCESS_DE , RQESTDE , REQUST_SJ FROM COMTHPROGRMCHANGEDTLS WHERE 1=1
AND PROCESS_STTUS_CODE like #{searchKeyword}
AND PROCESS_STTUS_CODE = #{searchKeyword}
AND RQESTDE between #{searchKeywordFrom} and #{searchKeywordTo}
AND RQESTER_ID like '%'||#{searchKeyword}||'%'
ORDER BY REQUST_NO ASC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(REQUST_NO) AS "totcnt" FROM COMTHPROGRMCHANGEDTLS WHERE 1=1
AND PROCESS_STTUS_CODE like #{searchKeyword}
AND PROCESS_STTUS_CODE = #{searchKeyword}
AND RQESTDE between #{searchKeywordFrom} and #{searchKeywordTo}
AND RQESTER_ID like '%'||#{searchKeyword}||'%'
