mapper.insert:

			INSERT INTO COMTHPROGRMCHANGEDTLS
				(  PROGRM_FILE_NM
              , REQUST_NO
              , RQESTER_ID
              , CHANGE_REQUST_CN
              , RQESTDE
              , REQUST_SJ 
              ,PROCESS_STTUS_CODE)
			VALUES (  #{progrmFileNm}
                 , #{rqesterNo}
                 , #{rqesterPersonId}
                 , #{changerqesterCn}
                 , #{rqesterDe}
                 , #{rqesterSj} 
                 , 'A')

mapper.update:

			UPDATE COMTHPROGRMCHANGEDTLS
			SET RQESTER_ID         = #{rqesterPersonId} 
			   , CHANGE_REQUST_CN  = #{changerqesterCn}
            , RQESTDE        = #{rqesterDe}       
            , REQUST_SJ        = #{rqesterSj} 
			WHERE PROGRM_FILE_NM=#{progrmFileNm}
			AND   REQUST_NO    =#{rqesterNo}

mapper.delete:

DELETE FROM COMTHPROGRMCHANGEDTLS
WHERE PROGRM_FILE_NM=#{progrmFileNm}
AND   REQUST_NO    =#{rqesterNo}

mapper.update:

			UPDATE COMTHPROGRMCHANGEDTLS
			SET REQUST_PROCESS_CN = #{rqesterProcessCn} 
           , OPETR_ID           = #{opetrId}         
           , PROCESS_STTUS_CODE = #{processSttus}    
           , PROCESS_DE         = #{processDe}       
			WHERE PROGRM_FILE_NM=#{progrmFileNm}
			AND   REQUST_NO    =#{rqesterNo}

mapper.select:
SELECT PROGRM_FILE_NM AS progrmFileNm , REQUST_NO AS rqesterNo , RQESTER_ID AS rqesterPersonId , CHANGE_REQUST_CN AS changerqesterCn , REQUST_PROCESS_CN AS rqesterProcessCn , OPETR_ID AS opetrId , PROCESS_STTUS_CODE AS processSttus , PROCESS_DE AS processDe , RQESTDE AS rqesterDe , REQUST_SJ AS rqesterSj FROM COMTHPROGRMCHANGEDTLS WHERE 1=1
AND binary(PROCESS_STTUS_CODE) like #{searchKeyword}
AND binary(PROCESS_STTUS_CODE) = #{searchKeyword}
AND RQESTDE between #{searchKeywordFrom} and #{searchKeywordTo}
AND binary(RQESTER_ID) like CONCAT('%', #{searchKeyword}, '%')
ORDER BY REQUST_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) AS totcnt FROM COMTHPROGRMCHANGEDTLS WHERE 1=1
AND binary(PROCESS_STTUS_CODE) like #{searchKeyword}
AND binary(PROCESS_STTUS_CODE) = #{searchKeyword}
AND RQESTDE between #{searchKeywordFrom} and #{searchKeywordTo}
AND binary(RQESTER_ID) like CONCAT('%', #{searchKeyword}, '%')
