mapper.select:
SELECT @RNUM:=@RNUM+1 AS rowCount , A.CTSNN_ID AS ctsnnId , A.USER_ID AS usid , A.CTSNN_CODE AS ctsnnCd , A.REQST_DE AS reqstDe , A.CTSNN_NM AS ctsnnNm , A.TRGTER_NM AS trgterNm , A.BRTHDY AS brth , A.OCCRRNC_DE AS occrrDe , A.RELATE AS relate , A.RM AS remark , A.SANCTNER_ID AS sanctnerId , A.CONFM_AT AS confmAt , A.SANCTN_DT AS sanctnDt , A.RETURN_RESN AS returnResn , A.INFRML_SANCTN_ID AS infrmlSanctnId , C.USER_NM AS usNm , (select mst.USER_NM from COMVNUSERMASTER mst where mst.ESNTL_ID = SANCTNER_ID) AS sanctnerNm , (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM054' and code = A.CTSNN_CODE) AS ctsnnCdNm , (select info.ORGNZT_NM from COMTNORGNZTINFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS orgnztNm FROM (select @RNUM:=0) R, COMTNCTSNNMANAGE A, COMVNUSERMASTER C WHERE 1=1 AND A.USER_ID = C.ESNTL_ID AND A.CTSNN_CODE = #{searchKeyword}
AND A.OCCRRNC_DE BETWEEN CAST( DATE_FORMAT(#{searchFromDate},'%Y%m%d' ) AS CHAR(8) ) AND CAST( DATE_FORMAT(#{searchToDate},'%Y%m%d' ) AS CHAR(8) )
AND C.USER_NM like CONCAT('%', #{searchNm}, '%')
AND A.CONFM_AT like CONCAT('%', #{searchConfmAt}, '%')
ORDER BY FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT @RNUM:=@RNUM+1 AS rowCount , A.CTSNN_ID AS ctsnnId , A.USER_ID AS usid , A.CTSNN_CODE AS ctsnnCd , A.REQST_DE AS reqstDe , A.CTSNN_NM AS ctsnnNm , A.TRGTER_NM AS trgterNm , A.BRTHDY AS brth , A.OCCRRNC_DE AS occrrDe , A.RELATE AS relate , A.RM AS remark , A.SANCTNER_ID AS sanctnerId , A.CONFM_AT AS confmAt , A.SANCTN_DT AS sanctnDt , A.RETURN_RESN AS returnResn , A.INFRML_SANCTN_ID AS infrmlSanctnId , C.USER_NM AS usNm , (select mst.USER_NM from COMVNUSERMASTER mst where mst.ESNTL_ID = SANCTNER_ID) AS sanctnerNm , (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM054' and code = A.CTSNN_CODE) AS ctsnnCdNm , (select info.ORGNZT_NM from COMTNORGNZTINFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS orgnztNm FROM (select @RNUM:=0) R, COMTNCTSNNMANAGE A, COMVNUSERMASTER C WHERE 1=1 AND A.SANCTNER_ID = #{sanctnerId} AND A.USER_ID = C.ESNTL_ID AND A.CTSNN_CODE = #{searchKeyword}
AND A.OCCRRNC_DE BETWEEN CAST( DATE_FORMAT(#{searchFromDate},'%Y%m%d' ) AS CHAR(8) ) AND CAST( DATE_FORMAT(#{searchToDate},'%Y%m%d' ) AS CHAR(8) )
AND C.USER_NM like CONCAT('%', #{searchNm}, '%')
AND A.CONFM_AT like CONCAT('%', #{searchConfmAt}, '%')
ORDER BY FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
