mapper.select:
SELECT A.SEXDSTN_CODE, B.CODE_NM AS SEXDSTN_CODE_NM, A.EXTRL_HR_NM, A.EMAIL_ADRES, A.OCCP_TY_CODE, C.CODE_NM AS OCCP_TY_CODE_NM, A.PSITN_INSTT_NM, A.EXTRL_HR_ID, A.EVENT_ID, A.BRTHDY, A.AREA_NO, A.MIDDLE_TELNO, A.END_TELNO, DATE_FORMAT(A.FRST_REGIST_PNTTM,'%Y-%m-%d') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, DATE_FORMAT(A.LAST_UPDT_PNTTM,'%Y-%m-%d') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNEXTRLHRINFO A LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM014' AND USE_AT='Y') b ON a.SEXDSTN_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM034' AND USE_AT='Y') c ON a.OCCP_TY_CODE = c.CODE WHERE 1=1
AND A.EVENT_ID = #{eventId}
AND EXTRL_HR_NM LIKE CONCAT ('%', #{searchKeyword},'%')
AND PSITN_INSTT_NM LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
