mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rowCount, ALL_LIST.* FROM ( SELECT mge.EVENT_ID AS eventId , mge.EVENT_SE AS eventSe , mge.EVENT_NM AS eventNm , mge.EVENT_PURPS AS eventPurps , mge.EVENT_BGNDE AS eventBeginDe , mge.EVENT_ENDDE AS eventEndDe , mge.EVENT_AUSPC_INSTT_NM AS eventAuspcInsttNm, mge.EVENT_MNGT_INSTT_NM AS eventMngtInsttNm , mge.EVENT_PLACE AS eventPlace , mge.EVENT_CN AS eventCn , mge.CT_OCCRRNC_AT AS ctOccrrncAt , mge.PARTCPT_CT AS partcptCt , mge.PSNCPA AS psncpa , mge.REFRN_URL AS refrnUrl , mge.RCEPT_BGNDE AS rceptBeginDe , mge.RCEPT_ENDDE AS rceptEndDe , to_date(mge.EVENT_ENDDE, 'YYYYMMDD')-to_date(mge.EVENT_BGNDE,'YYYYMMDD')+1 as eventTemp1 , (select count(APPLCNT_ID) from COMTNEVENTATDRN where EVENT_ID = mge.EVENT_ID) as eventTemp2, (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM053' and code = mge.EVENT_SE) as eventTemp3, atdrn.APPLCNT_ID AS applcntId , atdrn.CONFM_AT AS confmAt FROM COMTNEVENTMANAGE mge left join COMTNEVENTATDRN atdrn ON atdrn.EVENT_ID = mge.EVENT_ID and atdrn.APPLCNT_ID = #{applcntId} WHERE 1=1 AND (TO_CHAR(sysdate, 'yyyymmdd') BETWEEN RCEPT_BGNDE AND RCEPT_ENDDE or atdrn.confm_At is not null)
AND mge.EVENT_SE like '%'||#{searchSe}||'%'
AND #{searchKeyword} BETWEEN SUBSTR(mge.EVENT_BGNDE, 0, 6) AND SUBSTR(mge.EVENT_ENDDE, 0, 6)
AND mge.EVENT_BGNDE like '%'||#{searchKeyword}||'%'
AND mge.EVENT_NM like '%'||#{searchNm}||'%'
AND
atdrn.CONFM_AT is null
atdrn.CONFM_AT = #{searchConfmAt}
atdrn.CONFM_AT = #{searchConfmAt}
atdrn.CONFM_AT = #{searchConfmAt}
ORDER BY mge.EVENT_ID DESC ) ALL_LIST ) WHERE rowCount > #{firstIndex} AND rowCount <= #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNEVENTMANAGE mge left join COMTNEVENTATDRN atdrn ON atdrn.EVENT_ID = mge.EVENT_ID and atdrn.APPLCNT_ID = #{searchKeyword} WHERE 1=1 AND (TO_CHAR(sysdate, 'yyyymmdd') BETWEEN RCEPT_BGNDE AND RCEPT_ENDDE or atdrn.confm_At is not null)
AND mge.EVENT_SE like '%'||#{searchSe}||'%'
AND #{searchKeyword} BETWEEN SUBSTR(mge.EVENT_BGNDE, 0, 6) AND SUBSTR(mge.EVENT_ENDDE, 0, 6)
AND mge.EVENT_BGNDE like '%'||#{searchKeyword}||'%'
AND mge.EVENT_NM like '%'||#{searchNm}||'%'
AND
atdrn.CONFM_AT is null
atdrn.CONFM_AT = #{searchConfmAt}
atdrn.CONFM_AT = #{searchConfmAt}
atdrn.CONFM_AT = #{searchConfmAt}

SELECT * FROM ( SELECT ROWNUM AS rowCount, ALL_LIST.* FROM ( SELECT mge.EVENT_ID AS eventId , mge.EVENT_SE AS eventSe , mge.EVENT_NM AS eventNm , mge.EVENT_PURPS AS eventPurps , mge.EVENT_BGNDE AS eventBeginDe , mge.EVENT_ENDDE AS eventEndDe , mge.EVENT_AUSPC_INSTT_NM AS eventAuspcInsttNm, mge.EVENT_MNGT_INSTT_NM AS eventMngtInsttNm , mge.EVENT_PLACE AS eventPlace , mge.EVENT_CN AS eventCn , mge.CT_OCCRRNC_AT AS ctOccrrncAt , mge.PARTCPT_CT AS partcptCt , mge.PSNCPA AS psncpa , mge.REFRN_URL AS refrnUrl , mge.RCEPT_BGNDE AS rceptBeginDe , mge.RCEPT_ENDDE AS rceptEndDe , atdrn.INFRML_SANCTN_ID AS infrmlSanctnId , atdrn.REQST_DE AS reqstDe , to_date(mge.EVENT_ENDDE, 'YYYYMMDD')-to_date(mge.EVENT_BGNDE,'YYYYMMDD')+1 as eventTemp1 , (select count(APPLCNT_ID) from COMTNEVENTATDRN where EVENT_ID = mge.EVENT_ID) AS eventTemp2, (select code_nm from COMTCCMMNDETAILCODE WHERE code_id ='COM053' AND code= mge.EVENT_SE) AS eventSeNm, atdrn.APPLCNT_ID AS applcntId , atdrn.CONFM_AT AS confmAt , atdrn.SANCTNER_ID AS sanctnerId , (select USER_NM from COMVNUSERMASTER WHERE ESNTL_ID = atdrn.SANCTNER_ID) AS sanctnerNm, (select USER_NM from COMVNUSERMASTER WHERE ESNTL_ID = atdrn.APPLCNT_ID) AS applcntNm, TO_CHAR(atdrn.SANCTN_DT, 'yyyy-mm-dd') AS sanctnDt FROM COMTNEVENTMANAGE mge, COMTNEVENTATDRN atdrn WHERE atdrn.EVENT_ID = mge.EVENT_ID AND atdrn.SANCTNER_ID = #{sanctnerId}
AND mge.EVENT_SE like '%'||#{searchSe}||'%'
AND #{searchKeyword} BETWEEN SUBSTR(mge.EVENT_BGNDE, 0, 6) AND SUBSTR(mge.EVENT_ENDDE, 0, 6)
AND mge.EVENT_BGNDE like '%'||#{searchKeyword}||'%'
AND mge.EVENT_NM like '%'||#{searchNm}||'%'
ORDER BY mge.EVENT_ID DESC ) ALL_LIST ) WHERE rowCount > #{firstIndex} AND rowCount <= #{firstIndex} + #{recordCountPerPage}

mapper.delete:

            DELETE FROM COMTNEVENTATDRN
			WHERE  APPLCNT_ID     = #{applcntId}
			AND    EVENT_ID       = #{eventId}

mapper.select:

            SELECT  * 
              FROM  (
            	SELECT ROWNUM AS rowCount, ALL_LIST.* 
              		FROM  (
						select mas.user_nm    as eventTemp1,  
						      (select orgnzt_nm  from COMTNORGNZTINFO where orgnzt_ID = mas.orgnzt_ID) AS eventTemp2,
						       mas.user_email as eventTemp3, 
						       atd.confm_at   as eventTemp4
						from   COMTNEVENTATDRN atd, COMVNUSERMASTER mas
						where  atd.applcnt_ID = ESNTL_ID
						and    atd.EVENT_ID = #{eventId}
						ORDER BY atd.EVENT_ID DESC
                       	) ALL_LIST
                    )
          WHERE  rowCount  > #{firstIndex}
            AND  rowCount <= #{firstIndex} + #{recordCountPerPage}
