mapper.update:
UPDATE COMTNNOTERECPTN 
SET
	OPEN_YN='Y',
	LAST_UPDT_PNTTM=sysdate(),
	LAST_UPDUSR_ID=#{lastUpdusrId}
WHERE 1=1
	AND NOTE_ID=#{noteId}
	AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}
	AND NOTE_RECPTN_ID=#{noteRecptnId}

mapper.select:
SELECT NOTE.NOTE_ID, NOTE_RNS.NOTE_TRNSMIT_ID, NOTE_RNS.NOTE_RECPTN_ID, NOTE_TNS.TRNSMITER_ID, NOTE.NOTE_SJ, NOTE.NOTE_CN, NOTE.ATCH_FILE_ID, NOTE_RNS.RCVER_ID RCVER_ID, NOTE_RNS.OPEN_YN, NOTE_RNS.RECPTN_SE, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = NOTE_RNS.RCVER_ID) RCVER_NM, (SELECT EMPLYR_ID FROM COMTNEMPLYRINFO WHERE ESNTL_ID = NOTE_RNS.RCVER_ID) RCVER_IDS, DATE_FORMAT(NOTE_RNS.FRST_REGIST_PNTTM,'%Y-%m-%d %H:%i:%s') FRST_REGISTER_PNTTM FROM COMTNNOTE NOTE LEFT OUTER JOIN COMTNNOTETRNSMIT NOTE_TNS ON ( NOTE.NOTE_ID=NOTE_TNS.NOTE_ID ) LEFT OUTER JOIN COMTNNOTERECPTN NOTE_RNS ON ( NOTE_TNS.NOTE_ID=NOTE_RNS.NOTE_ID AND NOTE_TNS.NOTE_TRNSMIT_ID=NOTE_RNS.NOTE_TRNSMIT_ID ) WHERE 1=1 AND NOTE_RNS.RCVER_ID = #{rcverId}
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE EMPLYR_ID = #{searchKeyword})
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM = #{searchKeyword})
AND NOTE.NOTE_SJ LIKE CONCAT('%', #{searchKeyword}, '%')
AND NOTE.NOTE_CN LIKE CONCAT('%', #{searchKeyword}, '%')
AND DATE_FORMAT(NOTE_TNS.FRST_REGIST_PNTTM,'%Y-%m-%d') >= #{searchFromDate} AND DATE_FORMAT(NOTE_TNS.FRST_REGIST_PNTTM,'%Y-%m-%d') <= #{searchToDate}
ORDER BY NOTE.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNNOTE NOTE LEFT OUTER JOIN COMTNNOTETRNSMIT NOTE_TNS ON ( NOTE.NOTE_ID=NOTE_TNS.NOTE_ID ) LEFT OUTER JOIN COMTNNOTERECPTN NOTE_RNS ON ( NOTE_TNS.NOTE_ID=NOTE_RNS.NOTE_ID AND NOTE_TNS.NOTE_TRNSMIT_ID=NOTE_RNS.NOTE_TRNSMIT_ID ) WHERE 1=1 AND NOTE_RNS.RCVER_ID = #{rcverId}
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE EMPLYR_ID = #{searchKeyword})
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM = #{searchKeyword})
AND NOTE.NOTE_SJ LIKE CONCAT('%', #{searchKeyword}, '%')
AND NOTE.NOTE_CN LIKE CONCAT('%', #{searchKeyword}, '%')
AND DATE_FORMAT(NOTE_TNS.FRST_REGIST_PNTTM,'%Y-%m-%d') >= #{searchFromDate} AND DATE_FORMAT(NOTE_TNS.FRST_REGIST_PNTTM,'%Y-%m-%d') <= #{searchToDate}

SELECT 
COUNT(NOTE_ID) CNT
FROM COMTNNOTERECPTN  
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}

mapper.delete:

DELETE FROM COMTNNOTERECPTN 
WHERE 1=1 
AND NOTE_ID=#{noteId} AND 
NOTE_TRNSMIT_ID=#{noteTrnsmitId} 
AND NOTE_RECPTN_ID=#{noteRecptnId}


DELETE FROM COMTNNOTETRNSMIT 
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}



DELETE FROM COMTNNOTERECPTN 
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId} 
AND NOTE_RECPTN_ID=#{noteRecptnId};

DELETE FROM COMTNNOTETRNSMIT
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId};

DELETE FROM COMTNNOTE WHERE NOTE_ID=#{noteId};
