mapper.delete:

DELETE FROM COMTNRECENTSRCHWRD
WHERE 1=1
AND RECENT_SRCHWRD_ID=#{srchwrdId}
AND SRCHWRD_MANAGE_ID=#{srchwrdManageId}

mapper.select:
SELECT A.SRCHWRD_MANAGE_ID, A.RECENT_SRCHWRD_ID, A.RECENT_SRCHWRD_NM SRCHWRD_NM, A.FRST_REGISTER_ID, DATE_FORMAT(A.FRST_REGIST_PNTTM,'%Y-%m-%d') FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNRECENTSRCHWRD A WHERE 1=1 AND SRCHWRD_MANAGE_ID=#{srchwrdManageId}
AND RECENT_SRCHWRD_NM LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
