mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.RSS_ID, A.TRGET_SVC_NM, A.TRGET_SVC_TABLE, A.HDER_TITLE, A.HDER_LINK, A.HDER_DC HDER_DESCRIPTION, A.HDER_TAG, A.HDER_ETC, A.BDT_TITLE, A.BDT_LINK, A.BDT_DC BDT_DESCRIPTION, A.BDT_TAG, A.BDT_ETC_TAG BDT_ETC, A.FRST_REGISTER_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNRSS A WHERE 1=1
AND A.TRGET_SVC_NM LIKE '%' || #{searchKeyword} || '%'
AND A.TRGET_SVC_TABLE LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE ROWNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
