mapper.insert:

			INSERT INTO COMTNSITELIST
				  (SITE_ID
				  , SITE_URL
 				  , SITE_NM
				  , SITE_DC
				  , SITE_THEMA_CL_CODE
				  , ACTVTY_AT
				  , USE_AT
				  , FRST_REGIST_PNTTM
				  , FRST_REGISTER_ID
				  , LAST_UPDT_PNTTM
				  , LAST_UPDUSR_ID)
			VALUES ( #{siteId}
				  , #{siteUrl}
				  , #{siteNm}
				  , #{siteDc}
				  , #{siteThemaClCode}
				  , #{actvtyAt}
				  , #{useAt}
				  , sysdate()
				  , #{frstRegisterId}
				  , sysdate()
				  , #{lastUpdusrId} )

mapper.update:

			UPDATE	COMTNSITELIST	SET
				    SITE_ID=#{siteId}
				  , SITE_URL=#{siteUrl}
 				  , SITE_NM=#{siteNm}
				  , SITE_DC=#{siteDc}
