mapper.select:
SELECT CNTNTS_ID, CNTNTS_NM, CNTC_URL, CNTNTS_USE_AT, CNTNTS_LINK_URL, CNTNTS_DC FROM COMTNINDVDLPGECNTNTS WHERE 1=1
AND CNTNTS_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
ORDER BY CNTNTS_ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}


SELECT CNTNTS_ID, CNTNTS_NM, CNTC_URL, CNTNTS_USE_AT, CNTNTS_LINK_URL, CNTNTS_DC 
FROM COMTNINDVDLPGECNTNTS
WHERE CNTNTS_ID=#{cntntsId}

mapper.insert:

INSERT INTO COMTNINDVDLPGECNTNTS 
    (
    	CNTNTS_ID, CNTNTS_NM, CNTC_URL, CNTNTS_USE_AT, CNTNTS_LINK_URL, CNTNTS_DC
    )
VALUES 
	(
		#{cntntsId},#{cntntsNm},#{cntcUrl},#{cntntsUseAt},#{cntntsLinkUrl},#{cntntsDc}
	)

mapper.update:
UPDATE COMTNINDVDLPGECNTNTS SET
CNTNTS_NM = #{cntntsNm}, CNTC_URL = #{cntcUrl}, CNTNTS_USE_AT = #{cntntsUseAt}, CNTNTS_LINK_URL = #{cntntsLinkUrl}, CNTNTS_DC = #{cntntsDc}
WHERE CNTNTS_ID=#{cntntsId}
