mapper.select:
SELECT FAQ_ID, QESTN_SJ, QESTN_CN, ANSWER_CN, RDCNT, ATCH_FILE_ID, TO_CHAR(FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGIST_PNTTM, FRST_REGISTER_ID, TO_CHAR(LAST_UPDT_PNTTM,'YYYY-MM-DD') LAST_UPDUSR_PNTTM, LAST_UPDUSR_ID FROM ( SELECT ROWNUM RNUM,FAQ.* FROM ( SELECT FAQ_ID, QESTN_SJ, QESTN_CN, ANSWER_CN, RDCNT, ATCH_FILE_ID, FRST_REGIST_PNTTM , FRST_REGISTER_ID, LAST_UPDT_PNTTM , LAST_UPDUSR_ID FROM COMTNFAQINFO WHERE 1=1
AND QESTN_SJ LIKE '%' || #{searchWrd} || '%'
ORDER BY FAQ_ID DESC ) FAQ ) WHERE ROWNUM <= #{recordCountPerPage} and RNUM > #{firstIndex}

mapper.update:

			UPDATE	COMTNFAQINFO	SET
				    RDCNT= RDCNT + 1				  			
 			WHERE  FAQ_ID=#{faqId}


			UPDATE	COMTNFAQINFO	SET
					FAQ_ID=#{faqId} 
				  , QESTN_SJ=#{qestnSj}
				  , QESTN_CN=#{qestnCn}
				  , ANSWER_CN=#{answerCn}
				  , ATCH_FILE_ID=#{atchFileId}				  			
				  , LAST_UPDT_PNTTM=sysdate
				  , LAST_UPDUSR_ID=#{lastUpdusrId}
 			WHERE  FAQ_ID=#{faqId}
