mapper.select:
SELECT A.POLL_ID, A.POLL_NM, A.POLL_BGNDE POLL_BEGIN_DE, A.POLL_ENDDE POLL_END_DE, A.POLL_KND POLL_KIND_CODE, A.POLL_DSUSE_ENNC, A.POLL_ATMC_DSUSE_ENNC, A.FRST_REGISTER_ID, DATE_FORMAT(A.FRST_REGIST_PNTTM,'%Y-%m-%d') FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNONLINEPOLLMANAGE A WHERE 1=1
AND POLL_NM LIKE CONCAT ('%', #{searchKeyword},'%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE COMTNONLINEPOLLIEM 
SET 
	POLL_IEM_NM = #{pollIemNm}  
WHERE POLL_IEM_ID=#{pollIemId}
