mapper.select:
SELECT A.POLL_ID, A.POLL_NM, A.POLL_BGNDE POLL_BEGIN_DE, A.POLL_ENDDE POLL_END_DE, A.POLL_KND POLL_KIND_CODE, A.POLL_DSUSE_ENNC, A.POLL_ATMC_DSUSE_ENNC, A.FRST_REGISTER_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYYmmdd') FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNONLINEPOLLMANAGE A WHERE 1=1 AND A.POLL_ID IN (SELECT DISTINCT(POLL_ID) POLL_ID FROM COMTNONLINEPOLLIEM) AND A.POLL_DSUSE_ENNC = 'N' AND (( A.POLL_ATMC_DSUSE_ENNC = 'N') OR (A.POLL_ATMC_DSUSE_ENNC = 'Y' AND TO_CHAR(NOW(),'YYYYmmdd') >= REPLACE(A.POLL_BGNDE,'-','') AND TO_CHAR(NOW(),'YYYYmmdd') <= REPLACE(A.POLL_ENDDE,'-','')))
AND POLL_NM LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}


SELECT COUNT(*)
FROM COMTNONLINEPOLLRESULT
WHERE POLL_ID = #{pollId}
AND FRST_REGISTER_ID = #{frstRegisterId}
