mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM ( SELECT A.QESTNR_ID, A.QUSTNR_SJ AS QESTNR_SJ, A.QUSTNR_PURPS AS QESTNR_PURPS, A.QUSTNR_WRITNG_GUIDANCE_CN AS QESTNR_WRITNG_GUIDANCE_CN, TO_CHAR(TO_DATE(A.QUSTNR_BGNDE, 'YYYYMMDD'),'YYYY-MM-DD') QESTNR_BEGIN_DE, TO_CHAR(TO_DATE(A.QUSTNR_ENDDE, 'YYYYMMDD'),'YYYY-MM-DD') QESTNR_END_DE, A.QUSTNR_TRGET AS QESTNR_TRGET, A.QUSTNR_TMPLAT_ID AS QESTNR_TMPLAT_ID, A.FRST_REGIST_PNTTM AS FRST_REGISTER_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNQESTNRINFO A WHERE 1=1
AND QUSTNR_SJ LIKE '%' || #{searchKeyword} || '%'
AND FRST_REGISTER_ID IN (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM LIKE '%' || #{searchKeyword} || '%' )
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
