mapper.select:

SELECT QUSTNR_SJ
FROM COMTNQESTNRINFO
WHERE QESTNR_ID = #{qestnrId}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.QUSTNR_QESITM_ID AS QESTNR_QESITM_ID, A.QESTNR_ID, (SELECT QUSTNR_SJ FROM COMTNQESTNRINFO WHERE QESTNR_ID=A.QESTNR_ID) QESTNR_SJ, A.QESTN_SN, A.QESTN_TY_CODE, A.QESTN_CN, A.MXMM_CHOISE_CO, A.QUSTNR_TMPLAT_ID AS QESTNR_TMPLAT_ID, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD') FRST_REGISTER_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD') LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNQUSTNRQESITM A WHERE 1=1
AND QESTN_CN LIKE '%' || #{searchKeyword} || '%'
AND MXMM_CHOISE_CO LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
