mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM ( SELECT A.QESTNR_ID, A.QUSTNR_SJ AS QESTNR_SJ, A.QUSTNR_PURPS AS QESTNR_PURPS, A.QUSTNR_WRITNG_GUIDANCE_CN AS QESTNR_WRITNG_GUIDANCE_CN, TO_CHAR(TO_DATE(A.QUSTNR_BGNDE, 'YYYYMMDD'),'YYYY-MM-DD') QESTNR_BEGIN_DE, TO_CHAR(TO_DATE(A.QUSTNR_ENDDE, 'YYYYMMDD'),'YYYY-MM-DD') QESTNR_END_DE, A.QUSTNR_TRGET AS QESTNR_TRGET, A.QUSTNR_TMPLAT_ID AS QESTNR_TMPLAT_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGISTER_PNTTM, A.FRST_REGISTER_ID, B.USER_NM FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNQESTNRINFO A JOIN COMTNEMPLYRINFO B ON B.ESNTL_ID = A.FRST_REGISTER_ID WHERE 1=1
AND QUSTNR_SJ LIKE '%' || #{searchKeyword} || '%'
AND B.USER_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.delete:

DELETE FROM COMTNQUSTNRRSPNSRESULT
WHERE 1=1
AND QUSTNR_RSPNS_RESULT_ID = #{qestnrQesrspnsId}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM ( SELECT QUSTNR_RSPNS_RESULT_ID AS QESTNR_QESRSPNS_ID, ( SELECT QESTN_TY_CODE FROM COMTNQUSTNRQESITM WHERE 1=1 AND QUSTNR_QESITM_ID = A.QUSTNR_QESITM_ID ) QESTN_TY_CODE, ( SELECT QESTN_CN FROM COMTNQUSTNRQESITM WHERE 1=1 AND QUSTNR_QESITM_ID = A.QUSTNR_QESITM_ID ) QESTN_CN, ( SELECT IEM_CN FROM COMTNQUSTNRIEM WHERE 1=1 AND QUSTNR_IEM_ID = A.QUSTNR_IEM_ID ) IEM_CN, A.QUSTNR_IEM_ID, A.QUSTNR_QESITM_ID, A.QESTNR_ID, A.RESPOND_ANSWER_CN, A.RESPOND_NM, A.ETC_ANSWER_CN, A.QUSTNR_TMPLAT_ID QESTNR_TMPLAT_ID, A.FRST_REGIST_PNTTM FRST_REGISTER_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNQUSTNRRSPNSRESULT A WHERE 1=1
AND ETC_ANSWER_CN LIKE '%' || #{searchKeyword} || '%'
AND RESPOND_ANSWER_CN LIKE '%' || #{searchKeyword} || '%'
AND RESPOND_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
