mapper.update:
UPDATE COMTNQUSTNRTMPLAT SET
QUSTNR_TMPLAT_IMAGE_INFO = #{qestnrTmplatImagepathnm},
QUSTNR_TMPLAT_TY = #{qestnrTmplatTy}, QUSTNR_TMPLAT_DC = #{qestnrTmplatCn}, QUSTNR_TMPLAT_PATH_NM = #{qestnrTmplatCours}, LAST_UPDT_PNTTM = sysdate, LAST_UPDUSR_ID = #{lastUpdusrId} WHERE QUSTNR_TMPLAT_ID = #{qestnrTmplatId}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.QUSTNR_TMPLAT_ID AS QESTNR_TMPLAT_ID, A.QUSTNR_TMPLAT_TY AS QESTNR_TMPLAT_TY, A.FRST_REGIST_PNTTM AS FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.FRST_REGISTER_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.QUSTNR_TMPLAT_IMAGE_INFO AS QESTNR_TMPLAT_IMAGEPATHNM, A.QUSTNR_TMPLAT_DC AS QESTNR_TMPLAT_CN, A.QUSTNR_TMPLAT_PATH_NM AS QESTNR_TMPLAT_COURS FROM COMTNQUSTNRTMPLAT A WHERE 1=1
AND QUSTNR_TMPLAT_DC LIKE '%' || #{searchKeyword} || '%'
AND QUSTNR_TMPLAT_TY LIKE '%' || #{searchKeyword} || '%'
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
