mapper.select:
SELECT CPYRHT_ID, CPYRHT_PRTC_POLICY_CN, FRST_REGIST_PNTTM , FRST_REGISTER_ID, LAST_UPDT_PNTTM , LAST_UPDUSR_ID FROM COMTNCPYRHTINFO WHERE 1=1
AND CPYRHT_PRTC_POLICY_CN LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY CPYRHT_ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

			UPDATE	COMTNCPYRHTINFO	SET 
				   	CPYRHT_PRTC_POLICY_CN=#{cpyrhtPrtcPolicyCn}
				  , LAST_UPDT_PNTTM=NOW()
				  , LAST_UPDUSR_ID=#{lastUpdusrId}
 			WHERE  CPYRHT_ID=#{cpyrhtId}
