mapper.update:

UPDATE COMTNENTRPRSMBER 
SET 
       ENTRPRS_MBER_PASSWORD   =  #{entrprsMberPassword}
     , CHG_PWD_LAST_PNTTM = sysdate()
WHERE  ESNTL_ID  = #{uniqId}

mapper.select:
SELECT uniqId, userTy, userId , cmpnyNm, userNm, emailAdres, areaNo, middleTelno, endTelno, moblphonNo, groupId, sttus, sbscrbDe FROM( SELECT ESNTL_ID as uniqId, 'USR02' as userTy, ENTRPRS_MBER_ID as userId, CMPNY_NM as cmpnyNm, APPLCNT_NM as userNm, APPLCNT_EMAIL_ADRES as emailAdres, AREA_NO as areaNo, ENTRPRS_MIDDLE_TELNO as middleTelno, ENTRPRS_END_TELNO as endTelno, '' as moblphonNo, GROUP_ID as groupId, ENTRPRS_MBER_STTUS as sttus, DATE_FORMAT(SBSCRB_DE,'%Y-%m-%d') as sbscrbDe FROM COMTNENTRPRSMBER ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE '%' #{searchKeyword} '%'
AND userNm LIKE '%' #{searchKeyword} '%'
ORDER BY sbscrbDe DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
