mapper.update:

UPDATE COMTNENTRPRSMBER
SET 
       ENTRPRS_MBER_PASSWORD   =  #{entrprsMberPassword}
     , CHG_PWD_LAST_PNTTM = sysdate
WHERE  ESNTL_ID  = #{uniqId}

mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID as UNIQ_ID, 'USR02' as USER_TY, ENTRPRS_MBER_ID as USER_ID, CMPNY_NM as CMPNY_NM, APPLCNT_NM as USER_NM, APPLCNT_EMAIL_ADRES as EMAIL_ADRES, AREA_NO as AREA_NO, ENTRPRS_MIDDLE_TELNO as MIDDLE_TELNO, ENTRPRS_END_TELNO as END_TELNO, '' as MOBLPHON_NO, GROUP_ID as GROUP_ID, ENTRPRS_MBER_STTUS as STTUS, SBSCRB_DE as SBSCRB_DE FROM COMTNENTRPRSMBER WHERE 1=1
AND ENTRPRS_MBER_STTUS LIKE #{sbscrbSttus}
AND ENTRPRS_MBER_ID LIKE '%' || #{searchKeyword} || '%'
AND APPLCNT_NM LIKE '%' ||#{searchKeyword}|| '%'
ORDER BY 13 DESC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
