mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID as UNIQ_ID, 'USR01' as USER_TY, MBER_ID as USER_ID, MBER_NM as USER_NM, MBER_EMAIL_ADRES as EMAIL_ADRES, AREA_NO as AREA_NO, MIDDLE_TELNO as MIDDLE_TELNO, END_TELNO as END_TELNO, MBTLNUM as MOBLPHON_NO, GROUP_ID as GROUP_ID, MBER_STTUS as STTUS, SBSCRB_DE as SBSCRB_DE FROM COMTNGNRLMBER WHERE 1=1
AND MBER_STTUS LIKE #{sbscrbSttus}
AND MBER_ID LIKE '%' || #{searchKeyword} || '%'
AND MBER_NM LIKE '%' ||#{searchKeyword}|| '%'
ORDER BY 12 DESC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:

UPDATE COMTNGNRLMBER 
SET 
       PASSWORD   =  #{password}
     , CHG_PWD_LAST_PNTTM = sysdate
WHERE  ESNTL_ID  = #{uniqId}
