mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID as UNIQ_ID, 'USR03' as USER_TY, EMPLYR_ID as USER_ID, USER_NM as USER_NM, EMAIL_ADRES as EMAIL_ADRES, AREA_NO as AREA_NO, HOUSE_MIDDLE_TELNO as MIDDLE_TELNO, HOUSE_END_TELNO as END_TELNO, MBTLNUM as MOBLPHON_NO, GROUP_ID as GROUP_ID, EMPLYR_STTUS_CODE as STTUS, SBSCRB_DE as SBSCRB_DE FROM COMTNEMPLYRINFO WHERE 1=1
AND EMPLYR_STTUS_CODE LIKE #{sbscrbSttus}
AND EMPLYR_ID LIKE '%' || #{searchKeyword} || '%'
AND USER_NM LIKE '%' ||#{searchKeyword}|| '%'
ORDER BY 12 DESC ) TB ) Z WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:

UPDATE COMTNEMPLYRINFO 
SET 
       PASSWORD   =  #{password}
     , CHG_PWD_LAST_PNTTM = SYSDATETIME
WHERE  ESNTL_ID  = #{uniqId}
