mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.SYS_ID , A.WEBSVC_KND , A.SITE_URL , B.CODE_NM HTTP_STTUS_CODE , TO_CHAR(A.CREAT_DT,'YYYY-MM-DD HH24:MI:SS') CREAT_DT , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTNHTTPMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.HTTP_STTUS_CODE = B.CODE WHERE 1 = 1
AND A.MNGR_NM LIKE '%'||#{searchKeyword}||'%'
AND B.CODE_NM = #{searchKeyword}
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(A.SYS_ID) as totcnt FROM COMTNHTTPMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.HTTP_STTUS_CODE = B.CODE WHERE 1 = 1
AND A.MNGR_NM LIKE '%'||#{searchKeyword}||'%'
AND B.CODE_NM = #{searchKeyword}

mapper.insert:

    INSERT 
      INTO  COMTNHTTPMON
         (  SYS_ID
         ,  WEBSVC_KND
         ,  SITE_URL
         ,  HTTP_STTUS_CODE
         ,  CREAT_DT		         
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{sysId}
         ,  #{webKind}
         ,  #{siteUrl}
         ,  #{httpSttusCd}
         ,  TO_DATE(#{creatDt}, 'yyyymmddhh24miss')		         
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  SYSDATETIME		         		         
         ,  #{lastUpdusrId}
         ,  SYSDATETIME
         )

mapper.update:

UPDATE  COMTNHTTPMON 
   SET  WEBSVC_KND          = #{webKind}
     ,  SITE_URL          = #{siteUrl}
   	 ,  MNGR_NM        	  = #{mngrNm}
     ,  MNGR_EMAIL_ADRES   = #{mngrEmailAddr}
     ,  LAST_UPDT_PNTTM = SYSDATETIME
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  SYS_ID            = #{sysId}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.LOG_ID , A.SYS_ID , A.WEBSVC_KND , B.SITE_URL , C.CODE_NM HTTP_STTUS_CODE , TO_CHAR(A.CREAT_DT,'YYYY-MM-DD HH24:MI:SS') CREAT_DT , A.LOG_INFO , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTHHTTPMONLOGINFO A, COMTNHTTPMON B, COMTCCMMNDETAILCODE C WHERE A.SYS_ID = B.SYS_ID AND A.HTTP_STTUS_CODE = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND TO_CHAR(A.CREAT_DT,'YYYY-MM-DD HH24MISS') BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND A.WEBSVC_KND LIKE '%'||#{searchKeyword}||'%'
AND C.CODE_NM = #{searchKeyword}
ORDER BY A.CREAT_DT DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(A.SYS_ID) as totcnt FROM COMTHHTTPMONLOGINFO A, COMTNHTTPMON B, COMTCCMMNDETAILCODE C WHERE A.SYS_ID = B.SYS_ID AND A.HTTP_STTUS_CODE = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND TO_CHAR(A.CREAT_DT,'YYYY-MM-DD HH24MISS') BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND A.WEBSVC_KND LIKE '%'||#{searchKeyword}||'%'
AND C.CODE_NM = #{searchKeyword}

mapper.insert:

    INSERT 
      INTO  COMTHHTTPMONLOGINFO
         (  LOG_ID
         ,  SYS_ID
         ,  WEBSVC_KND
         ,  SITE_URL
         ,  HTTP_STTUS_CODE
         ,  CREAT_DT
         ,  LOG_INFO		         
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{logId}
         ,  #{sysId}
         ,  #{webKind}
         ,  #{siteUrl}
         ,  #{httpSttusCd}		         
         ,  TO_DATE(#{creatDt}, 'yyyymmddhh24miss')
         ,  #{logInfo}		         
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  #{frstRegisterPnttm}		         		         
         ,  #{lastUpdusrId}
         ,  SYSDATETIME
         )

mapper.update:

UPDATE  COMTNHTTPMON 
   SET  HTTP_STTUS_CODE     = #{httpSttusCd}
     ,  CREAT_DT          = TO_DATE(#{creatDt}, 'yyyymmddhh24miss')
     ,  LAST_UPDT_PNTTM = SYSDATETIME
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  SYS_ID            = #{sysId}
