mapper.delete:


DELETE FROM COMTNNTWRKSVCMNTRNG 
WHERE SYS_IP = #{sysIp}
AND SYS_PORT::VARCHAR = #{sysPort}

mapper.select:
 	SELECT 
		COUNT(SYS_IP) as cnt
	FROM COMTNNTWRKSVCMNTRNG 
	WHERE SYS_IP = #{sysIp}
		AND SYS_PORT::VARCHAR = #{sysPort}

mapper.update:

UPDATE COMTNNTWRKSVCMNTRNG 
SET  
	MNTRNG_STTUS=#{mntrngSttus},
	CREAT_DT=TO_TIMESTAMP(#{creatDt}, 'YYYYmmddHH24MISS'),
	LAST_UPDT_PNTTM = NOW(),
	LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE SYS_IP = #{sysIp}
	AND SYS_PORT::VARCHAR = #{sysPort}
