mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( SELECT A.PROXY_ID, A.PROXY_NM, A.PROXY_IP, A.PROXY_PORT, A.TRGET_SVC_NM, A.SVC_DC, A.SVC_IP, A.SVC_PORT, A.SVC_STTUS, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM072' AND CODE = A.SVC_STTUS) AS SVC_STTUS_NM, TO_CHAR(A.FRST_REGIST_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNPROXYINFO A WHERE 1 = 1
AND A.PROXY_NM LIKE '%'||#{strProxyNm}||'%'
ORDER BY A.PROXY_ID DESC ) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( SELECT A.PROXY_ID, A.LOG_ID, B.PROXY_NM, A.CLNT_PORT, A.CLNT_IP, TO_CHAR(A.CONECT_TIME, 'yyyy-mm-dd HH24:mi:ss') AS CONECT_TIME, TO_CHAR(A.FRST_REGIST_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNPROXYLOGINFO A, COMTNPROXYINFO B WHERE A.PROXY_ID = B.PROXY_ID
AND TO_CHAR(A.CONECT_TIME, 'YYYYMMDD') BETWEEN #{strStartDate} AND #{strEndDate}
ORDER BY A.LAST_UPDT_PNTTM DESC ) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}
