mapper.select:
SELECT A.PROXY_ID, A.LOG_ID, B.PROXY_NM, A.CLNT_PORT, A.CLNT_IP, DATE_FORMAT(A.CONECT_TIME, '%Y-%m-%d %T') AS CONECT_TIME, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d %T') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, DATE_FORMAT(A.LAST_UPDT_PNTTM, '%Y-%m-%d %T') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNPROXYLOGINFO A, COMTNPROXYINFO B WHERE A.PROXY_ID = B.PROXY_ID
AND DATE_FORMAT(A.CONECT_TIME, '%Y%m%d') BETWEEN #{strStartDate} AND #{strEndDate}
ORDER BY A.LAST_UPDT_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
