mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( SELECT SERVER_ID, SERVER_NM, SERVER_IP, SERVER_PORT, FTP_ID, FTP_PASSWORD, SYNCHRN_LC, REFLCT_AT, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID FROM COMTNSYNCHRNSERVERINFO WHERE 1 = 1
AND SERVER_NM LIKE '%'||#{strSynchrnServerNm}||'%'
ORDER BY SERVER_ID DESC ) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.update:
UPDATE COMTNSYNCHRNSERVERINFO
   SET REFLCT_AT = #{reflctAt}
 WHERE SERVER_ID = #{serverId}
